# Function to convert time strings like '1 day', '2 hours' to seconds
def get_seconds(time_str):
    time_parts = time_str.split()
    value = int(time_parts[0])
    unit = time_parts[1].lower()
    
    if unit.endswith('s'):
        unit = unit[:-1]
    
    if unit == 'day':
        return value * 86400  # 1 day = 86400 seconds
    elif unit == 'hour':
        return value * 3600   # 1 hour = 3600 seconds
    elif unit == 'minute':
        return value * 60     # 1 minute = 60 seconds
    elif unit == 'month':
        return value * 2629800  # Assuming 1 month = 30.44 days = 2629800 seconds
    elif unit == 'year':
        return value * 31557600  # Assuming 1 year = 365.25 days = 31557600 seconds
    else:
        return 0  # Invalid unit

